generated.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

*::before,
*::after {
  box-sizing: border-box;
}

ul,
ol {
  list-style: none;
}

a {
  color: inherit;
  text-decoration: none;
}

img {
  max-width: 100%;
  max-height: 100%;
}

body {
  background-color: #eee;
}

.globalHeader {
  margin: 20px;
}
.searchForm {
  display: flex;
  justify-content: space-between;
  max-width: 450px;
}

.searchForm > input {
  margin-right: 10px;
  padding: 0 10px;
  width: 100%;
  height: 40px;
  font-size: 16px;
  border: none;
  border: 1px solid #ddd;
  border-radius: 4px;
}
.searchForm > button {
  white-space: nowrap;
  padding: 0 24px;
  font-size: 16px;
  border-radius: 4px;
  border: none;
  background-color: #06f;
  color: white;
  cursor: pointer;
}
.globalMain {
}

.siteList {
  margin: 0 20px;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
}

.siteList > li {
}

.siteList .site {
  width: 160px;
  background-color: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 1px solid #dddddd;
  border-radius: 4px;
  margin-bottom: 10px;
  padding: 22px 0;
  position: relative;
}

.siteList .site > .logo {
  width: 64px;
  height: 64px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 64px;
  text-transform: uppercase;
}

.siteList .site > .link {
  font-size: 14px;
}

.siteList .site > .close {
  position: absolute;
  top: 5px;
  right: 10px;
}

.siteList .addButton {
  width: 160px;
  padding: 22px 0;
  background-color: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  border: 1px solid #dddddd;
  cursor: pointer;
  border-radius: 4px;
}

.siteList .addButton .icon {
  width: 64px;
  height: 64px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.siteList .addButton .text {
  font-size: 14px;
}

@media (pointer: fine) {
  .globalHeader {
    margin-top: 40px;
    margin-bottom: 40px;
  }
  .searchForm {
    max-width: 400px;
    margin-left: auto;
    margin-right: auto;
  }

  .globalMain {
    max-width: 900px;
    margin-left: auto;
    margin-right: auto;
  }

  .siteList {
    justify-content: flex-start;
    margin-left: 0;
    margin-right: -25px;
  }

  .siteList > li {
    margin-right: 25px;
  }

  .siteList .site > .close {
    display: none;
  }

  .siteList .site:hover > .close {
    display: block;
  }
}
